changeSet.sql:
create table if not exists admin
(
    id       bigint generated by default as identity primary key,
    username varchar(255)
);

create table if not exists client
(
    id       bigint generated by default as identity primary key,
    username varchar(255),
    amount   bigint
);

create table if not exists tour
(
    id    bigint generated by default as identity primary key,
    count bigint,
    name  varchar(255),
    price bigint
);

insert into admin (username)
values ('myadmin');

insert into client (username, amount)
values ('myclient', 1000);

INSERT INTO tour (count, name, price)
VALUES (5, 'shopping', 100),
       (10, 'relax', 300),
       (15, 'excursion', 200);
